const BASE_URL = "https://api.green-api.com";

type TBody = Record<string, string>;


async function request(
  method: string,
  userId: string,
  apiMethod: string,
  userApiToken: string,
  body?: TBody | undefined,
  receiptId?: number | undefined,
  ):Promise<any> { // any заменить на конкретную структуру данных ApiResponse для каждого промиса

  const url = `${BASE_URL}/waInstance${userId}/${apiMethod}/${userApiToken}`

  const headers: Record<string, string> = {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "http://localhost:3000, http://localhost:3000/home",
    "User-Agent": "PostmanRuntime/7.32.2",
    "Connection": "keep-alive",
    "Accept-Encoding": "gzip, deflate, br",
    "Accept": "*/*",
    "mode": "no-cors",
  }

  try {
    const response = await fetch(
      receiptId ? url + `/${receiptId}` : url,
      {
        headers,
        method,
        mode: 'cors',
        body: JSON.stringify(body),
      }
    );
    return response.json();
  } catch (error) {
    return console.error(`${apiMethod} response error: `, error);
  }
}

export function sendMessage(userId: string, userApiToken: string, body: TBody) {
  return request('POST', userId, 'sendMessage', userApiToken, body);
};

export function receiveNotification(userId: string, userApiToken: string){
  return request('GET', userId, 'receiveNotification', userApiToken)
};

export function deleteNotification (userId: string, userApiToken: string, _: undefined, receiptId: number) {
  return request ('DELETE', userId, 'deleteNotification', userApiToken, _, receiptId);
}
