import React, {
  useState,
  useCallback,
  useEffect,
  useContext
} from "react";
import { useNavigate } from "react-router-dom";
import { CurrentUserContext } from '../../AppContext';
import {TContextValue} from '../../models/context'
import { sendMessage } from '../../utils/requests';

import Input from '../../components/common/Input';
import Button from '../../components/common/Button';

import './Login.css';

export const Login: React.FC = () => {

  const body = 
  {
    "chatId": "79615065555@c.us",
    "message": "I use Green-API to send this message to you!"
  };

  const headers = {
    'Content-Type': 'application/json',
    "Access-Control-Allow-Origin": "*"
  };

  const response = fetch(
    "https://api.green-api.com/waInstance101821549/SendMessage/d38ad31b01ce4c3781f0ddb44ed28680b67800968899417386",
   {
    method: 'POST',
    // body: JSON.stringify(body),
    headers: [['Content-Type', 'application/json'], ["Access-Control-Allow-Origin", "*"]],
    credentials: 'include',
    mode:'no-cors'
  }).then(data => console.warn(data)).catch(err => console.error(err))

//   sendMessage("POST",
//   "https://api.green-api.com/waInstance101821549/sendMessage/d38ad31b01ce4c3781f0ddb44ed28680b67800968899417386",
//   body,
// ).then(data => console.log(data)).catch(err => console.log(err))

  const navigate = useNavigate();
  const CurrentUserContextValue = useContext<TContextValue>(CurrentUserContext);
  const [userId, setUserId] = useState('');
  const [userApiToken, setUserApiToken] = useState('');
  const [idError, setIdError] = useState('');
  const [tokenError, setTokenError] = useState('');

  const handleChangeId = useCallback((_: unknown, value: string): void => {
    setUserId(value);
  }, []);

  const handleChangeToken = useCallback((_: unknown, value: string): void => {
    setUserApiToken(value);
  }, []);

  const handleLogin = useCallback((): void => {
    if (!userId) setIdError('Введите ваш id')
    if (!userApiToken) setTokenError('Введите ваш token')
    if (userId && userApiToken) {
      CurrentUserContextValue?.setCurrentUserData({
        id: userId,
        apiToken: userApiToken
      });
      navigate('/home');
    }
  }, [userId, userApiToken, navigate, CurrentUserContextValue]);

  useEffect((): void => {
    if(userId) setIdError('');
    if(userApiToken) setTokenError('');
  }, [userId, userApiToken]);

  return (
  <div className="login">
    <Input
      placeholder="Введите Ваш Id"
      value={userId}
      onChange={handleChangeId}
      message={idError}
      fullwidth
    />
    <Input
      placeholder="Введите Ваш Token"
      value={userApiToken}
      onChange={handleChangeToken}
      message={tokenError}
      fullwidth
    />
    <Button
      text="Войти"
      onClick={handleLogin}
    />
  </div>  );
};
