import React, { useCallback, useState } from "react";

import { Input } from "../../components/common/Input/Input";
import { Button } from "../../components/common/Button/Button";

import './Home.css'

const сheckPhoneInput = /^\d+$/;

type TChat = {id: string, messages: string[]}
type TChats = Record<string,TChat>

export const Home: React.FC = () => {

  const [phone, setPhone] = useState('');
  const [chats, setChats] = useState<TChats>({});

  const handleChangePhone = useCallback((_: unknown, value: string) => {
      setPhone(value);
  }, []);

  const isButtonEnabled = !chats[phone];

  const handleClickCreateChat = useCallback(() => {
      if (сheckPhoneInput.test(phone)) {
        setChats((prev) => {
        if (!prev[phone]) {
          const newChats = {...prev, [phone]: {
          id: phone,
          messages: []
              }}
          setPhone('')
          return newChats
        } return prev;
        } );
      }
      }, [phone]);

      const handleClickChat = useCallback(() => {

      }, []);


  console.log(phone);


  return (
    <div className="home">
      <div className="home__chat-panel">
        <div className="home__chat-panel__header">
          <Input
            placeholder="Введите 11 цифр номера телефона"
            value={phone}
            onChange={handleChangePhone}
            isErrorMessage={false}
            onEnter={handleClickCreateChat}
          />
          <Button
          text="OK"
          onClick={handleClickCreateChat}
          disabled={!isButtonEnabled}
          />
        </div>
        <ul className="home__chat-panel__chats">
          {Object.keys(chats).map((chat) => <li onClick={handleClickChat} className="home__chat-panel__chat-card" key={chat}> {chat} </li>)}
         </ul>
      </div>
      <div className="home__conversation-panel">
      </div>
    </div>
  );
};
