import React, { createContext, useState } from 'react';
import { TUserData } from './models/user';

type TAppContextProps = {
  children: React.ReactNode;
};

export type TContextValue = {
  currentUserData: TUserData | null,
  setCurrentUserData: React.Dispatch<React.SetStateAction<TUserData | null>>
} | null;

export const CurrentUserContext = createContext<TContextValue>({currentUserData: null, setCurrentUserData: () => {}});

export const AppContext = ({ children }: TAppContextProps) => {

const [currentUserData, setCurrentUserData] = useState<TUserData | null> (null);

return (
  <CurrentUserContext.Provider 
    value={{
      currentUserData,
      setCurrentUserData,
    }}>
      {children}
  </CurrentUserContext.Provider>
);

};
