import { TMessage } from "../models/message";
import { request } from '../utils/requests/request';



export const apiSendMessage: (
  phone: string,
  message: TMessage,
  userId: string
) => Promise<Record<string, string>> = async (phone, message, userId) =>  {

const body = {
  chatId: phone + '@c.us',
  message: message.text,
}
const a = await request('POST', userId, 'sendMessage', body);
return await a.json();
};


