           message: message.text,
      }
      console.log(body);
      return request('POST', userId, 'sendMessage', body);
    };


    console.log("userId", userId)

  return (
    <div className="home">
      <div className="home__chat-panel">
        <div className="home__chat-panel__header">
          <Input
