import React, {
  useState,
  useCallback,
  useEffect,
  useContext
} from "react";
import { useNavigate } from "react-router-dom";
import { CurrentUserContext } from '../../AppContext';
import {TContextValue} from '../../models/context'

import Input from '../../components/common/Input';
import Button from '../../components/common/Button';

import './Login.css';

export const Login: React.FC = () => {

  const navigate = useNavigate();
  const CurrentUserContextValue = useContext<TContextValue>(CurrentUserContext);
  const [userId, setUserId] = useState('');
  const [userApiToken, setUserApiToken] = useState('');
  const [idError, setIdError] = useState('');
  const [tokenError, setTokenError] = useState('');

  const handleChangeId = useCallback((_: unknown, value: string) => {
    setUserId(value);
  }, []);

  const handleChangeToken = useCallback((_: unknown, value: string) => {
    setUserApiToken(value);
  }, []);

  const handleLogin = useCallback(() => {
    if (!userId) setIdError('Введите ваш id')
    if (!userApiToken) setTokenError('Введите ваш token')
    if (userId || userApiToken) {
      CurrentUserContextValue?.setCurrentUserData({
        id: userId,
        apiToken: userApiToken
      });
      console.log(CurrentUserContextValue?.currentUserData)
      navigate('/home');
    }
  }, [userId, userApiToken, navigate]);

  useEffect(() => {
    if(userId) setIdError('');
    if(userApiToken) setTokenError('');
  }, [userId, userApiToken]);

  return (
  <div className="login">
    <Input
      placeholder="Введите Ваш Id"
      value={userId}
      onChange={handleChangeId}
      message={idError}
    />
    <Input
      placeholder="Введите Ваш Token"
      value={userApiToken}
      onChange={handleChangeToken}
      message={tokenError}
    />
    <Button
      text="Войти"
      onClick={handleLogin}
    />
  </div>  );
};
