export async function sendMessage(method: string, url: string, body: Record<string, string>) {

  const headers = {
    "Content-Type" : "application/json",
  };

  const response = await fetch(url, {
    method: method,
    body: JSON.stringify(body),
    headers: headers,
    credentials: "include",
  });
    return await response.json();
};

