import { TMessage } from "../models/message";
import { request } from '../utils/requests/request';



export const apiSendMessage = async (phone: string, currentMessage: TMessage, userId: string) => {
  const body = {
       chatId: phone + '@c.us',
       message: currentMessage.text,
  }
  const result = await request('POST', userId, 'sendMessage', body);
  return await result.json();
};

export const apiReceiveNotification = async (userId: string) => {
  const result = await request('GET', userId, 'receiveNotification');
  return  result.json();
};
