import { handleActions } from "redux-actions";
import { TChat, TChats } from "../../models/chat";
import { TMessage } from "../../models/message";

import {
    ADD_NEW_CHAT,
    SET_CURRENT_CHAT,
    SEND_MESSAGE,
    SEND_MESSAGE_FAILURE,
    SEND_MESSAGE_PENDING,
    SEND_MESSAGE_SUCCESS,
} from "../actions/chat";

export type TChatState = {
  chats: TChats,
  currentChat: TChat,
  currentMessage: TMessage,
  isSendMessagePending: boolean,
  incomingMessageId: string,
  sendMessageError?: string,
};

export type TChatAction = {
  chats: TChats,
  currentChat: TChat,
  currentMessage: TMessage,
  isSendMessagePending: boolean,
  incomingMessageId: string,
  error?: string,
  phone: string,
};

const initialState: TChatState = {
  chats: {},
  currentChat: { phone: '', chatId: '', messages:[] },
  currentMessage: {
    messageId: '',
    text: '',
    timestamp: '',
    type: 'outgoing',
  },
  isSendMessagePending: false,
  incomingMessageId: '',
  sendMessageError: undefined,
};

export const chatReduser = handleActions<TChatState, TChatAction>({

[ADD_NEW_CHAT]: (state, { payload }) => { 
  const chats = {
    ...state.chats,
    [payload.phone]: { phone: payload.phone, chatId: '',messages: [] }
  }
  return ({
    ...state,
    chats,
 })
},

[SET_CURRENT_CHAT]: (state, { payload }) => ({
  ...state,
  currentChat: payload.currentChat,
}),

[SEND_MESSAGE]: (state, { payload }) => {

  const currentChat = {
    ...state.currentChat,
    messages:[...payload.currentChat.messages, payload.currentMessage]
  }

  return ({
  ...state,
  currentChat: currentChat,
  currentMessage: payload.currentMessage,
  })
},

[SEND_MESSAGE_PENDING]: (state, { payload }) => ({
  ...state,
  isSendMessagePending: payload.isSendMessagePending,
}),

[SEND_MESSAGE_SUCCESS]: (state, { payload }) => ({
  ...state,
  incomingMessageId: payload.incomingMessageId,
  sendMessageError: undefined,
}),

[SEND_MESSAGE_FAILURE]: (state, { payload }) => ({
    ...state,
    sendMessageError: payload.error,
})
}, initialState);

export default chatReduser;
