const BASE_URL = 'https://api.green-api.com/';

type TBody = Record<string, string>;

async function request(
  method: string,
  userId: string,
  apiMethod: string,
  userApiToken: string,
  body?: TBody,
  ) {

  const url = `${BASE_URL}waInstance${userId}/${apiMethod}/${userApiToken}`

  const headers = {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": '*',
    "User-Agent": "PostmanRuntime/7.32.2",
    "Connection": "keep-alive",
    "Accept-Encoding": "gzip, deflate, br",
    "Accept": "*/*",
    "Host": "<calculated when request is sent>",
    "Content-Length": "<calculated when request is sent>",
  }

  const response = await fetch(
    url,
    {
      headers,
      method,
      mode: 'no-cors',
      // credentials: 'include',
      body: JSON.stringify(body),
    }
  );
  return await response;
  // .then(data => console.log(`${apiMethod} response data: `, data))
  // .catch(error => console.error(`${apiMethod} response error: `, error));
}

export function sendMessage(userId: string, userApiToken: string, body: TBody) {
  return request('POST', userId, 'SendMessage', userApiToken, body)
  .then(data => console.log(`response data: `, data))
  .catch(error => console.error(`response error: `, error));;
};

export function receiveNotification(userId: string, userApiToken: string){
  return request('GET', userId, 'ReceiveNotification', userApiToken);
};
