export async function sendMessage(method: string, url: string, body: Record<string, string>) {

  const headers = {
    "Content-Type": "application/json"
  };

  const response = await fetch(url, {
    method: method,
    body: JSON.stringify(body),
    headers: headers,
  });
    return await response.json();
};

const body = 
  {
    "chatId": "11001234567@c.us",
