import React, { createContext, useState, ReactNode } from 'react';
import { TUserData } from './models/user';

type TContextValue = {
  currentUserData: Record<string, TUserData> | null;
  setCuurentUserData: React.Dispatch<React.SetStateAction<Record<string, TUserData> | null>>;
};

type TAppContextProps = {
  children: ReactNode;
};

export const CurrentUserContext = createContext<TContextValue | null>(null);

export const AppContext = ({ children }: TAppContextProps) => {

const [currentUserData, setCuurentUserData] = useState<Record<string, TUserData> | null>(null);

return (
  <CurrentUserContext.Provider 
    value={{
      currentUserData,
      setCuurentUserData,
    }}>
      {children}
  </CurrentUserContext.Provider>
);

};
