import React, { useCallback, } from "react";

import './Input.css';

type TInputProps = {
  type?: 'text'
  value: string,
  placeholder: string,
  disabled?: boolean,
  onChange: (event: React.ChangeEvent<HTMLInputElement> | undefined, value: string) => void,
  onKeyDown?: () => void;

}

export const Input: React.FC<TInputProps> = ({ 
  placeholder = '',
  value = '',
  type='text',
  disabled = false,
  onChange,
  onKeyDown,
}) => {

    const handleInputChange = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
        if (typeof onChange === 'function' && !disabled) {
          const newValue = event.target.value;
          onChange(event, newValue as never);
        }
      }, [onChange, type, disabled]);

    return (
        <div>
            <input
              type={type}
              className="input"
              placeholder={placeholder}
              value={value}
              onChange={handleInputChange}
              onKeyDown={onKeyDown}
              disabled={disabled}
            />
        </div>
    );
}
