import React, { useCallback, } from "react";

import './Input.css';

type TInputProps = {
  type?: 'text'
  value: string,
  message?: string,
  placeholder: string,
  disabled?: boolean,
  onEnter?: () => void,
  onChange: (event: React.ChangeEvent<HTMLInputElement> | undefined, value: string) => void,

}

export const Input: React.FC<TInputProps> = ({ 
  placeholder = '',
  value = '',
  type='text',
  message = '',
  disabled = false,
  onChange,
  onEnter,
}) => {

  const handleInputChange = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    if (typeof onChange === 'function' && !disabled) {
      const newValue = event.target.value;
      onChange(event, newValue as never);
    }
  }, [onChange, disabled]);

  const handleEnterDown = useCallback((event: React.KeyboardEvent<HTMLInputElement>) => {
    if (typeof onEnter === 'function' && !disabled && (event.code === 'Enter' || event.code === 'NumpadEnter')) {
      onEnter();
    }
  }, [disabled, onEnter]);

  return (
    <div className="input__wrapper">
      <input
        type={type}
        className="input"
        placeholder={placeholder}
        value={value}
        onChange={handleInputChange}
        onKeyDown={handleEnterDown}
        disabled={disabled}
      />
      <span
        className="input__message"
      >
        {message}
      </span>
    </div>
  );
}
