import { call, put, select } from 'typed-redux-saga';
import { takeEvery } from 'redux-saga/effects';
import { Action } from 'redux-actions';

import {
    SEND_MESSAGE,
    sendMessagePending,
    sendMessageSuccess,
    sendMessageFailure,
    RECEIVE_NOTIFICATION,
    receiveNotificationPanding,
} from '../actions/chat';
import { TMessage } from '../../models/message';
import { apiSendMessage, apiReceiveNotification } from '../../api';
import { TRootState } from '../reducers';
import { TUserState } from '../reducers/user';

function* sendMessage({ payload }: Action<{ phone: string, currentMessage: TMessage, userId: string}>) {
  try {
    yield put(sendMessagePending(true));
    const { idMessage } = yield* call(apiSendMessage, payload.phone, payload.currentMessage, payload.userId);
    if (idMessage) {
        yield put(sendMessageSuccess(idMessage, payload.currentMessage, payload.phone))};
  } catch (e) {
      yield put(sendMessageFailure('Не удалось отправить сообщение'));
  } finally {
      yield put(sendMessagePending(false));
  }
};

function* receiveNotification ():Generator {
  try {
    yield put(receiveNotificationPanding(true));
    const userId  = yield select(({ user }) => user.userId);
    console.log('userId', userId)
    // const notification = yield* call(apiReceiveNotification, userId);
  } catch (e) {

  }finally {

  }
};

export default function* chatSaga () {
    yield takeEvery(SEND_MESSAGE, sendMessage);
    yield takeEvery(RECEIVE_NOTIFICATION, receiveNotification);
}
