import React, { useCallback, useContext, useEffect, useState } from "react";
import {v4 as uuid} from 'uuid';

import { sendMessage, receiveNotification, deleteNotification } from '../../utils/requests';
import { Input } from "../../components/common/Input/Input";
import { Button } from "../../components/common/Button/Button";
import { CurrentUserContext } from "../../AppContext";
import { TChat, TChats} from '../../models/chat';
import { TContextValue } from "../../models/context";
import { MessageCard } from "../../components/common/Message";
import { TMessage } from '../../models/message'

import './Home.css'

const сheckPhoneInput = /^\d+$/;

export const Home: React.FC = () => {

  const [phone, setPhone] = useState('');
  const [chats, setChats] = useState<TChats>({});
  const [message, setMessage] = useState<TMessage>({id: '', text: '', timestamp: ''});
  const [currentChat, setCurrentChat] = useState<TChat>();
  const CurrentUserContextValue = useContext<TContextValue>(CurrentUserContext);

  const userId = CurrentUserContextValue?.currentUserData?.id;
  const userApiToken = CurrentUserContextValue?.currentUserData?.apiToken;

  const handleChangePhone = useCallback((_: unknown, value: string): void => {
      setPhone(value);
  }, []);

  const handleChangeMessage = useCallback((_: unknown, value: string): void => {
      setMessage({...message, text: value});
  }, [message]);

  const isButtonEnabled = !chats[phone];

  const handleClickCreateChat = useCallback((): void => {
      if (сheckPhoneInput.test(phone)) {
        setChats((prev) => {
        if (!prev[phone]) {
          const newChats = {...prev, [phone]: {
          id: phone,
          messages: []
              }}
          setPhone('')
          return newChats
        } return prev;
        } );
      }
      }, [phone]);

      const handleClickCurrentChat = useCallback((event: React.MouseEvent<HTMLLIElement>): void => {
        setMessage({id: '', text: '', timestamp: ''});
        const liElement = event.target as HTMLLIElement;
        const currentNumber = liElement.innerText;
        setCurrentChat(chats[currentNumber]);
      }, [chats]);

      const handleSendMessage = useCallback(() => {
        const messageData = {
          text: message.text,
          id: '',
          timestamp: `${+new Date()}`,
        }
        const currentNumber = currentChat?.id;
        const body = {
          chatId: currentNumber + '@c.us',
          message: message.text,
        };
        if (userId && userApiToken) {
          sendMessage(userId, userApiToken, body)
          .then(response => {
              const curentMessage = currentChat?.messages.find(data => data.timestamp === messageData.timestamp);
              if (curentMessage) curentMessage.id = response.idMessage;
              console.log('curentMessage', curentMessage);
            });
          };
        currentChat?.messages.push(messageData);
        setMessage({ id: '', timestamp: '', text: '' });
      }, [currentChat, userApiToken, userId, message]);

      // function deleteAllNotification () {
      //     if (userId && userApiToken) receiveNotification(userId, userApiToken)
      //     .then(response => {
      //       if (response.receiptId) (deleteNotification(userId, userApiToken, undefined, response.receiptId))
      //       .then(response => {
      //         if (response.result) deleteAllNotification();

      //       })
      //     }).catch(reject => console.log(reject))
      //   }


      deleteAllNotification();

    // useEffect(() => {
    //   if (userApiToken && userId) receiveNotification(userId, userApiToken)
    //   .then(response => {
    //     if (response.receiptId) {
    //       deleteNotification(userId, userApiToken, undefined, response.receiptId)
    //       .then(response => {
    //         if (response.result) receiveNotification(userId, userApiToken)
    //       });;
    //     }
    //   });
    // }, [userId, userApiToken]);


      console.log('userApiToken', CurrentUserContextValue?.currentUserData?.apiToken)
      console.log('userId', CurrentUserContextValue?.currentUserData?.id)
      console.log('currentNumber', currentChat?.id)
      console.log('message', message)
      console.log('chats', chats)
      console.log('body', {
        chatId: currentChat?.id + '@c.us',
        message,
      })
  return (
    <div className="home">
      <div className="home__chat-panel">
        <div className="home__chat-panel__header">
          <Input
            placeholder="Введите 11 цифр номера телефона"
            value={phone}
            onChange={handleChangePhone}
            isErrorMessage={false}
            onEnter={handleClickCreateChat}
          />
          <Button
          text="OK"
          onClick={handleClickCreateChat}
          disabled={!isButtonEnabled}
          />
        </div>
        <ul className="home__chat-panel__chats">
          {Object.keys(chats).map((chat) => <li onClick={ handleClickCurrentChat} className="home__chat-panel__chat-card" key={uuid()}> {chat} </li>)}
         </ul>
      </div>
      <div className="home__conversation-panel">
        <div className="home__conversation-panel__message-wrapper">
          {currentChat?.messages.map((chat) =><MessageCard>
              <span key={uuid()}>{chat.text}</span>
            </MessageCard>
            )}
        </div>
        <div className="home__conversation-panel__input-fields">
          <Input
            placeholder={!currentChat ? "Чтобы напечатать сообщение выберите чат" : "Введите текст сообщения"}
            onChange={handleChangeMessage}
            value={message.text}
            isErrorMessage={false}
            onEnter={handleSendMessage}
            disabled={!currentChat}
          />
          <Button
            text="Отправить"
            onClick={handleSendMessage}
            disabled={!message}
            />
          </div>
      </div>
    </div>
  );
};
