import React, { useState, useCallback } from "react";

import Input from '../../components/common/Input';
import Button from '../../components/common/Button';

import './Login.css';

export const Login: React.FC = () => {

  const [userId, setUserId] = useState('');
  const [userApiToken, setUserApiToken] = useState('');
  const [inputErrors, setInputErrors] = useState<{idErorr?: string, tokenError?: string}>(
    {
       idErorr:'',
       tokenError:'',
    }
  )

  const handleChangeId = useCallback((_: unknown, value: string) => {
    setUserId(value);
  }, []);

  const handleChangeToken = useCallback((_: unknown, value: string) => {
    setUserApiToken(value);
  }, []);

  const handleLogin = useCallback(() => {
    // if (!userId) setInputErrors({idErorr: 'Введите ваш id'})
    if (!userId) setInputErrors((prev) => {console.log(prev)})
    if (!userApiToken) setInputErrors({tokenError: 'Введите ваш token'})
  }, [userId, userApiToken]);


  return (
  <div className="login">
    <Input
      placeholder="Введите Ваш Id"
      value={userId}
      onChange={handleChangeId}
      message={inputErrors.idErorr}
    />
    <Input
      placeholder="Введите Ваш Token"
      value={userApiToken}
      onChange={handleChangeToken}
      message={inputErrors.idErorr}
    />
    <Button
      text="Войти"
      onClick={handleLogin}
    />
  </div>  );
};
