import { call, put, select } from 'typed-redux-saga';
import { takeEvery } from 'redux-saga/effects';
import { Action } from 'redux-actions';

import {
    SEND_MESSAGE,
    sendMessagePending,
    sendMessageSuccess,
    sendMessageFailure,
} from '../actions/chat';
import { TMessage } from '../../models/message';
import { apiSendMessage } from '../../api/index';
// import { TChat } from '../../models/chat';
// import { TRootState } from '../reducers';

function* sendMessage({ payload }: Action<{ phone: string, message: TMessage, userId: string}>) {
  yield put(sendMessagePending(true));
  // const phone = yield* select((state: TRootState) => state.user); // вытянул телефон из стейта
  const incomingMessageId = yield* call(apiSendMessage, payload.phone, payload.message, payload.userId);
  if(incomingMessageId.idMessage) console.log('incomingMessageId', incomingMessageId);

};

export default function* chatSaga () {
    yield takeEvery(SEND_MESSAGE, sendMessage);
}
